mapper.select:
select
from qizhe_scpc where qnamehao = #{pd.USERNAME}
and ( qname LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') or qgongsi LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') )
and DATE_FORMAT(dateMonth,'%Y-%m') = #{pd.dataMonth}
order by qgongsi desc

select
from qizhe_scpc where qgongsi = #{pd.USERNAME}
and ( qname LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') or qgongsi LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') )
and DATE_FORMAT(dateMonth,'%Y-%m') = #{pd.dataMonth}
order by qgongsi desc

select qscpc.id, aduit2.scpcid, qscpc.af, qscpc.bf, qscpc.cf, qscpc.df, qscpc.ef, qscpc.ff, qscpc.gf, qscpc.qname, qscpc.newdate, qscpc.beiyong3, qscpc.jinzhandate, qscpc.qgongsi, qscpc.bumen, qscpc.qnamehao, qscpc.qscore, qscpc.dateMonth, aduit2.projectName, aduit2.content1, aduit2.content2, aduit2.audit1, aduit2.audit2, aduit2.status1 from qizhe_audit2 aduit2 LEFT JOIN qizhe_scpc qscpc ON qscpc.id=aduit2.scpcid where aduit2.status1 != null or aduit2.status1 != ''
and ( qscpc.qname LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') or qscpc.qgongsi LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') )
and DATE_FORMAT(qscpc.dateMonth,'%Y-%m') = #{pd.dataMonth}
and aduit2.status1 = #{pd.status1}

select , qscpc.id, aduit2.scpcid, aduit2.projectName, aduit2.content1, aduit2.content2, aduit2.audit1, aduit2.audit2, aduit2.status1 from qizhe_audit2 aduit2 LEFT JOIN qizhe_scpc qscpc ON qscpc.id=aduit2.scpcid where aduit2.status1 != null or aduit2.status1 != ''
and ( qscpc.qname LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') or qscpc.qgongsi LIKE CONCAT(CONCAT('%', #{pd.KEYW}),'%') )
and DATE_FORMAT(qscpc.dateMonth,'%Y-%m') = #{pd.dataMonth}
